sp_widget.client_script:
function($scope,$rootScope,spUtil) {
	/* widget controller */
	var c = this;
	$rootScope.$on('SelectedNote',function(e,d){		
		c.server.get({action:'getnote',
									noteid:$rootScope.noteid
								 })
			.then(function(r){
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.noteID = r.data.note.sys_id;
		});
	});
	c.update = function(data){
		$rootScope.NewNoteid=c.data.noteID;				
		c.server.get({
			action:"updatenote",
			noteid:c.data.noteID,
			title:c.data.title,
			note:c.data.note
		}).then(function(r){					
		$rootScope.$emit('Bodyupdated',c.data.noteID);		

		});
	}
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	if (input && input.noteid) {
		var note = new GlideRecord('x_snc_createnotes_note');		
		if (note.get(input.noteid)){
			if (input.action=='getnote'){					
				data.note={};
				$sp.getRecordDisplayValues(data.note,note,"title, note, sys_id");			
			}
			else if (input.action=='updatenote'){
				note.title= input.title;
				note.note=input.note;
				note.update();
			}	
		}
	}
})();
