sp_widget.client_script:
function($rootScope,$scope,spUtil) {
  /* widget controller */
  var c = this;
	c.selecteditem = function(id){
	var selectedid= c.data.notes[id].sys_id;	
		$rootScope.noteid=selectedid;
	$rootScope.$emit('SelectedNote',selectedid)	;	

	// code to update Note list based if List body has been changed.
	$rootScope.$on('Bodyupdated',function(d,e){		
		c.server.update();			
	});

	}

}

sp_widget.script:
(function(){

	data.notes=[];
	var noteGr= new GlideRecord('x_snc_createnotes_note');
	noteGr.addQuery('user',gs.getUser().getID());
	noteGr.query();
	while(noteGr.next()){
		var noteObj={};
		noteObj.title = noteGr.getValue("title");
		noteObj.note=noteGr.getValue("note");
		noteObj.sys_id=noteGr.getValue("sys_id");
		data.notes.push(noteObj);			
	}		
})();
